const fs = require('fs');
const path = require('path');
const regenerate = require('regenerate');

const VERSION = '15.0.0';
const DEST = path.resolve(__dirname, '..', 'src', 'parser', 'unicode-regex.ts');

const idStart = regenerate()
  .add(
    require(`@unicode/unicode-${VERSION}/Binary_Property/ID_Start/code-points.js`),
  )
  .toRegExp();

const idContinue = regenerate()
  .add(
    require(`@unicode/unicode-${VERSION}/Binary_Property/ID_Continue/code-points.js`),
  )
  .toRegExp();

fs.writeFileSync(
  DEST,
  `
// GENERATED CODE - DO NOT MODIFY BY HAND
// (Generated by 'scripts/unicode-regex-gen.js')
export const unicodeIdStartReg = ${idStart};
export const unicodeIdContinueReg = ${idContinue};
  `.trim(),
);
